mapper.insert:

			SELECT 
				nvl(MAX(to_number(com_num)), 0) + 1 AS "comNum"
			FROM commment


	     insert into commment(com_num
                    , ctgr
                    , ctgr_num
                    , dep
                    , grp
                    , id
                    , cntn
                    , com_date)
            values(#{comNum}
                    , #{ctgr}
                    , '12'
                    , #{dep}

						, #{grp} 
						 ,#{conNum} 

                    ,#{id}
                    , #{cntn}
                    , sysdate)
