mapper.update:
UPDATE Member
NAME = #{name}, PWD = #{pwd}, Email = #{email}, TEL = #{tel}, birth_date = #{birthDate}
WHERE id=#{id}

UPDATE PHOTOGRAPHER

		SHOT_PLACE = #{shotPlace},
		ITR = #{itr},
		WORK_DAY = #{workDay}

WHERE PTG_ID=#{ptgId}

mapper.select:
SELECT DISTINCT p.res_num, r.res_cd, r.regi_date, o.ttl, p.ord_date, m.id, m.name, m.email, m.tel
FROM payment p, reser r, selected_op s, options o, member m, photographer ph
WHERE p.res_num = r.res_num
AND r.id = m.id
AND p.id = m.id
AND s.op_num = o.op_num
AND r.ptg_id = ph.ptg_id
AND ph.ptg_id = #{ptgId}
AND r.shot_time = #{shotTime}
AND r.shot_date =#{redate}
