mapper.select:
SELECT * FROM( SELECT ROWNUM RN, A.* FROM( SELECT DISTINCT NVL(Z.ZZIMSS,0) ZZIMS,P.*,M.* FROM PHOTOGRAPHER P, MEMBER M,(SELECT CTGR_NUM,COUNT(*) ZZIMSS FROM ZZIM GROUP BY CTGR_NUM ORDER BY 2 DESC) Z, FIELD F WHERE P.PTG_ID = M.ID AND M.ID = Z.CTGR_NUM(+) AND P.PTG_ID = F.PTG_ID
AND P.REGION_CD in #{vo.region}
AND F.FD_CD in #{vo.field}
AND M.NAME LIKE '%'||#{vo.searchWord}||'%'
ORDER BY P.REGI_DATE DESC ORDER BY M.NAME ORDER BY NVL(Z.ZZIMS,0) DESC
) A WHERE ROWNUM <= #{cri.pageNum} * #{cri.amount}) B WHERE RN > (#{cri.pageNum}-1) * #{cri.amount}

SELECT COUNT(DISTINCT P.PTG_ID) FROM PHOTOGRAPHER P, MEMBER M,(SELECT CTGR_NUM,COUNT(*) ZZIMS FROM ZZIM GROUP BY CTGR_NUM ORDER BY 2 DESC) Z, FIELD F WHERE P.PTG_ID = M.ID AND M.ID = Z.CTGR_NUM(+) AND P.PTG_ID = F.PTG_ID
AND P.REGION_CD in #{vo.region}
AND F.FD_CD in #{vo.field}
AND M.NAME LIKE '%'||#{vo.searchWord}||'%'

		SELECT * FROM( SELECT ROWNUM RN, A.* FROM( 

		SELECT DISTINCT P.PTG_ID,M.NAME,M.PROFILE
		FROM PHOTOGRAPHER P, MEMBER M , FIELD F
		WHERE
		P.PTG_ID = M.ID 
        AND P.PTG_ID = F.PTG_ID


					AND P.REGION_CD in 
						#{vo.region}



					AND F.FD_CD in 
						#{vo.field}




	       	AND M.NAME LIKE '%'||#{vo.searchWord}||'%'

	       	ORDER BY M.NAME

	 	) A WHERE ROWNUM <= #{cri.pageNum} * #{cri.amount}) B WHERE RN > (#{cri.pageNum}-1) * #{cri.amount}
