mapper.select:
select * from( select rownum rn, a.* from(
SELECT r.rev_Num, r.ctgr, r.ctgr_num, r.id, r.star, r.cntn, r.rev_date , count(p.pho_num) as "phoY" from photo p right outer join review r on p.ctgr_num = r.rev_num and p.ctgr = 'R' where r.ctgr = #{ctgr} and r.ctgr_num= #{ctgrNum} GROUP by r.rev_Num, r.ctgr, r.ctgr_num, r.star, r.cntn, r.id, r.rev_date having count(p.pho_num) > 0 ORDER by to_number(r.rev_num) desc ORDER by to_number(r.star) desc, to_number(r.rev_num) desc ORDER by r.rev_date desc, to_number(r.rev_num) desc
) a where rownum <= #{cri.pageNum} * #{cri.amount}) b where rn > (#{cri.pageNum}-1) * #{cri.amount}

mapper.update:
update review

star = #{star},
cntn = #{cntn}

where rev_num = #{revNum}

mapper.select:
select case when count(*) > 0 then 1 else 0 end "reserIs"
from reser
where id = #{id} and ptg_id = #{ptgId}
